import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      navigate("/habits");
    }
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password.length < 6) {
      setMessage("Password must be at least 6 characters.");
      return;
    }
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/api/login", { email, password });
      const { token } = response.data;
      localStorage.setItem("token", token);
      setMessage("Login successful!");
      navigate('/habits');
    } catch (error) {
      if (error.response) {
        setMessage(error.response.data.message);
      } else {
        setMessage("Login failed. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    // Added a container and utility classes for centering and vertical alignment
    <div className="container d-flex justify-content-center align-items-center min-vh-100">
      <div className="card p-4 bg-white" style={{ maxWidth: '400px', width: '100%' }}> {/* Constrain max-width */}
        <h3 className="text-center mb-4 fw-bold">Login</h3>
        <form onSubmit={handleSubmit}>
          <div className="mb-3 position-relative">
            <input
              type="email"
              className="form-control"
              placeholder="Email address"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="mb-3 position-relative">
            <input
              type="password"
              className="form-control"
              placeholder="Password"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              autoComplete="current-password" // Added for better UX
            />
          </div>
          <div className="d-grid mb-3">
            <button type="submit" className="btn btn-primary fw-semibold" disabled={loading}>
              {loading ? "Logging in..." : "Login"}
            </button>
          </div>
          {message && (
            <p
              className={`text-center fw-semibold ${
                message.toLowerCase().includes("success") ? "text-success" : "text-danger"
              }`}
            >
              {message}
            </p>
          )}
          <p className="text-center small text-muted mb-0">
            You do not have an account? <a href="/">Register</a>
          </p>
        </form>
      </div>
    </div>
  );
}

export default Login;
